// @source: https://github.com/william-alexander/count.git
function update(){var a=textarea.value;count["char"].textContent=a.length,count.word.textContent=word(a)}function word(a){for(var b=!1,c=0,d=0;d<a.length;++d){var e=b;b=!/\s/.test(a.charAt(d)),!e&&b&&++c}return c}var count={textarea:document.getElementById("textarea"),word:document.getElementById("word"),"char":document.getElementById("char")};window.addEventListener("load",update),textarea.addEventListener("input",update);