function update(){var a=textarea.value,b=a.length;count["char"].textContent=b,count.word.textContent=0===b?0:textarea.value.split(" ").length}var count={textarea:document.getElementById("textarea"),word:document.getElementById("word"),"char":document.getElementById("char")};window.addEventListener("load",update),textarea.addEventListener("keydown",update);