function process(){var a=textarea.value;update(count["char"],count.chars,a.length),update(count.word,count.words,word(a))}function update(a,b,c){a.textContent=c,1===c?b.classList.add("hidden"):b.classList.remove("hidden")}function word(a){for(var b=!1,c=0,d=0;d<a.length;++d){var e=b;b=!/\s/.test(a.charAt(d)),!e&&b&&++c}return c}var count={textarea:document.getElementById("textarea"),word:document.getElementById("word"),words:document.getElementById("words"),"char":document.getElementById("char"),chars:document.getElementById("chars")};window.addEventListener("load",process),textarea.addEventListener("input",process);